-- procedure sp_consultarDetalleVenta
-- -----------------------------------------------------

DELIMITER $$
USE `soft_mlmbd`$$
CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_consultarDetalleVenta`(Ventaid INT)
BEGIN
	SELECT P.ProductoID,
		   P.Nombre,
		   TP.Nombre,
		   P.Precio,
		   DV.Cantidad,
		   P.Descripcion
	FROM soft_mlmbd.detalleventa DV INNER JOIN soft_mlmbd.producto P ON DV.ProductoID = P.ProductoID
	INNER JOIN soft_mlmbd.tipoproducto TP ON P.TipoProductoID = TP.TipoProductoID WHERE DV.VentaID = Ventaid 
    ORDER BY P.ProductoID;
END$$

