CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_consultarVentas`()
BEGIN
    SELECT V.VentaID,
	   V.FechaVenta,
       E.EmpleadoID,
       E.Nombre as 'NombreEmpleado',
       V.Subtotal,
       V.Iva,
       V.PrecioTotal
	FROM soft_mlmbd.venta V INNER JOIN soft_mlmbd.empleado E ON V.EmpleadoID = E.EmpleadoID
